figure1 = figure('Name','IIR Filter');

% Create subplot
subplot1 = subplot(2,1,1,'Parent',figure1);
hold(subplot1,'on');

% Create multiple line objects using matrix input to plot
plot1 = plot(X1,YMatrix1,'Parent',subplot1);
set(plot1(1),'DisplayName','Eingangssignal');
set(plot1(2),'DisplayName','Ausgangssignal');

% Create ylabel
ylabel('u');

% Create xlabel
xlabel('t[s]');

% Create title
