% ylim(subplot1,[0 2750]);
box(subplot1,'on');
grid(subplot1,'on');
hold(subplot1,'off');
% Create legend
legend1 = legend(subplot1,'show');
set(legend1,...
    'Position',[0.741647244460747 0.60220507425136 0.153846151032648 0.0727848082403594]);

% Create subplot
subplot2 = subplot(2,1,2,'Parent',figure1);
hold(subplot2,'on');

% Create multiple line objects using matrix input to plot
plot2 = plot(X2,YMatrix2,'Parent',subplot2);
set(plot2(1),'DisplayName','Eingangsspektrum');
set(plot2(2),'DisplayName','Ausgangsspektrum');

% Create ylabel
ylabel('|FFT(u)|');

% Create xlabel
xlabel('f[Hz]');

% Create title
title('Spektrum');

% Uncomment the following line to preserve the Y-limits of the axes
% ylim(subplot2,[0 1000000]);
box(subplot2,'on');
grid(subplot2,'on');
hold(subplot2,'off');
% Create legend
legend(subplot2,'show');

